"use strict";var cleave=new Cleave(".phone-number",{numericOnly:true,blocks:[0,3,3,4],delimiters:["(",") ","-"]});"use strict";window.onload=function(){document.forms[0].reset();placeFocus()};function placeFocus(){document.forms[0].elements[0].focus()}var placeSearch,autocomplete;var componentForm={locality:"long_name",administrative_area_level_1:"short_name",postal_code:"short_name"};function initAutocomplete(){autocomplete=new google.maps.places.Autocomplete(document.getElementById("autocomplete"),{types:["geocode"]});autocomplete.setFields(["address_component"]);autocomplete.addListener("place_changed",fillInAddress)}function fillInAddress(){var place=autocomplete.getPlace();for(var component in componentForm){document.getElementById('"'.concat(component,'"')).value=""}for(var i=0;i<place.address_components.length;i++){var addressType=place.address_components[i].types[0];var fullAddress=place.address_components[0].long_name+" "+place.address_components[1].long_name;if(componentForm[addressType]){var val=place.address_components[i][componentForm[addressType]];document.getElementById(addressType).value=val}}document.getElementById("autocomplete").value=fullAddress}function geolocate(){if(navigator.geolocation){navigator.geolocation.getCurrentPosition(function(position){var geolocation={lat:position.coords.latitude,lng:position.coords.longitude};var circle=new google.maps.Circle({center:geolocation,radius:position.coords.accuracy});autocomplete.setBounds(circle.getBounds())})}}"use strict";document.getElementById("gform").onload=function(){window.scrollTo(0,100)};"use strict";var menuBtn=document.querySelector(".menu-btn");var menu=document.querySelector(".SiteHeader-nav");var menuNav=document.querySelector(".menu-nav");var menuBranding=document.querySelector(".menu-branding");var menuItems=document.querySelectorAll(".menu-item");var showMenu=false;menuBtn.addEventListener("click",toggleMenu);function toggleMenu(){if(!showMenu){menuBtn.classList.add("close");menu.classList.add("show");menuNav.classList.add("show");menuBranding.classList.add("show");menuItems.forEach(function(item){return item.classList.add("show")});showMenu=true}else{menuBtn.classList.remove("close");menu.classList.remove("show");menuNav.classList.remove("show");menuBranding.classList.remove("show");menuItems.forEach(function(item){return item.classList.remove("show")});showMenu=false}}
