"use strict";var menuBtn=document.querySelector(".menu-btn");var menu=document.querySelector(".SiteHeader-nav");var menuNav=document.querySelector(".menu-nav");var menuBranding=document.querySelector(".menu-branding");var menuItems=document.querySelectorAll(".menu-item");var showMenu=false;menuBtn.addEventListener("click",toggleMenu);function toggleMenu(){if(!showMenu){menuBtn.classList.add("close");menu.classList.add("show");menuNav.classList.add("show");menuBranding.classList.add("show");menuItems.forEach(function(item){return item.classList.add("show")});showMenu=true}else{menuBtn.classList.remove("close");menu.classList.remove("show");menuNav.classList.remove("show");menuBranding.classList.remove("show");menuItems.forEach(function(item){return item.classList.remove("show")});showMenu=false}}
